mapper.select:
select su.*,sd.name dept_name
from sys_user su
left join sys_dept sd
on sd.id=su.dept_id

  su.deleted=1

    and su.id=#{userId}


    and su.username like concat('%',#{username},'%')


    and su.nick_name like concat('%',#{nickName},'%')


    and su.status=#{status}


    and su.create_time >= #{startTime}


    and su.create_time <= #{endTime}

select count(1) from sys_user where dept_id in
#{item}
and deleted=1
